mapper.delete:
delete
from sys_user_role
where user_id = #{userId}
  and role_id in

    #{roleId}

delete
from sys_user_role
where role_id = #{roleId}
  and user_id in

    #{userId}

mapper.select:
select distinct sm.id, sm.menu_name, sm.parent_id, sm.order_num, sm.path, sm.component, sm.query, sm.is_frame, sm.is_cache, sm.menu_type, sm.visible, sm.perms, sm.icon from sys_user_role sur inner join sys_role_menu srm on sur.role_id = srm.role_id inner join sys_menu sm on sm.id = srm.menu_id where sm.`status` = '0'
and sur.user_id = #{userId}
order by parent_id, order_num
